class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9 + 7
        n = len(nums)

        @lru_cache(None)
        def dfs(index, last_value):
            # Базовый случай: если мы дошли до конца массива
            if index == n:
                return 1

            count = 0
            # Перебираем все возможные значения для arr1[index]
            for val in range(nums[index] + 1):
                if val >= last_value:
                    arr2_value = nums[index] - val
                    # Если arr2_value >= last_value (монотонность невозрастающего массива)
                    if index == 0 or arr2_value <= nums[index-1] - last_value:
                        count = (count + dfs(index + 1, val)) % MOD

            return count

        return dfs(0, -1)
