class Solution {
public:
    int dp[2001][51];
    int mod=1e9+7;
    int helper(vector<int>& nums,int i,int prev){
        if(i==nums.size())return 1;
        if(dp[i][prev]!=-1)return dp[i][prev];
        int a=prev;
        int b= i>0 ? nums[i-1]-a : 51;
//         i can select curr a from 0 to nums[i]
        int ans=0;
        for(int curr=0;curr<=nums[i];curr++){
            int curr2=nums[i]-curr;
            if(curr>=a && curr2<=b){
                ans=(ans+helper(nums,i+1,curr))%mod;
            }
        }
        return dp[i][prev]=ans%mod;

    }
    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof(dp));
        return helper(nums,0,0);
    }
};
