class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int mod = 1000000007;
        int n = nums.size();
        int maxi = 0;
        for(int i = 0; i < n; i++)
        {
            if(maxi<nums[i]) maxi = nums[i];
        }
        vector<vector<int>>dp(n,vector<int>(maxi+1,0));
        for(int i = n-1; i >= 0; i--)
        {
            for(int j = maxi; j>=0; j--)
            {
                if(i==n-1){
                    if(j>nums[i]) dp[i][j] = 0;
                    else dp[i][j] = 1;
                }
                else
                {
                    int ans = 0;
                    int f = j;
                    int s = nums[i] - j;
                    for(int k = j; k <= maxi; k++)
                    {
                        if(f<=k && s>=nums[i+1]-k) ans = (ans + dp[i+1][k])%mod;
                    }
                    dp[i][j] = ans;
                }
            }
        }
        int ans = 0;
        for(int i = 0; i <= maxi; i++)
        {
            ans = (ans + dp[0][i])%mod;
        }
        return ans;

    }
};
