from functools import cache
class Solution:
    def countOfPairs(self, nums: List[int]) -> int:

        n = len(nums)
        mod = 10**9 + 7

        @cache
        def func(i,a,b):
            if i == n:
                return 1
            count = 0
            for j in range(max(a,0),nums[i]+1):
                if abs(nums[i]-j) <= b:
                    count += func(i+1,j,abs(nums[i]-j))
            return count%mod

        ans = func(0,-float('inf'),float('inf'))
        return ans
