#include <vector>
using namespace std;

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int MOD = 1000000007;
        int n = nums.size();
        int maxVal = 50;

        vector<vector<vector<int>>> dp(n, vector<vector<int>>(maxVal + 1, vector<int>(maxVal + 1, 0)));

        for (int j = 0; j <= maxVal; ++j) {
            int k = nums[0] - j;
            if (k >= 0 && k <= maxVal) {
                dp[0][j][k] = 1;
            }
        }

        for (int i = 1; i < n; ++i) {
            for (int j = 0; j <= maxVal; ++j) {
                for (int k = 0; k <= maxVal; ++k) {
                    if (dp[i - 1][j][k] > 0) {
                        for (int jNew = 0; jNew <= j; ++jNew) {
                            int kNew = nums[i] - jNew;
                            if (kNew >= k && kNew <= maxVal) {
                                dp[i][jNew][kNew] = (dp[i][jNew][kNew] + dp[i - 1][j][k]) % MOD;
                            }
                        }
                    }
                }
            }
        }

        int result = 0;
        for (int j = 0; j <= maxVal; ++j) {
            for (int k = 0; k <= maxVal; ++k) {
                result = (result + dp[n - 1][j][k]) % MOD;
            }
        }

        return result;
    }
};
