class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9 + 7
        n = len(nums)

        dp = [[0] * 51 for _ in range(n)]

        for j in range(nums[0] + 1):
            dp[0][j] = 1

        for i in range(1, n):
            for j in range(nums[i] + 1):
                arr2 = nums[i] - j
                if arr2 <= 50:
                    for k in range(j + 1):
                        if nums[i-1] - k >= arr2:
                            dp[i][j] = (dp[i][j] + dp[i-1][k]) % MOD

        return sum(dp[-1][j] for j in range(nums[-1] + 1)) % MOD
