class Solution {
public:
    const int mod=1e9+7;
    long long dp[2002][55];
    long long solve(vector<int>&v,int i,int prev)
    {
        if(i==v.size())
        return 1;
        if(dp[i][prev]!=-1)
        return dp[i][prev];
        long long ans=0;
        for(int j=0;j<=v[i];j++)
        {
            int val=j;
            int val2=v[i]-j;
            if(prev<=val && val2<=v[i-1]-prev)
            {
                ans+=solve(v,i+1,val);
                ans%=mod;
            }
        }
        return dp[i][prev]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        long long int ans=0;
        memset(dp,-1,sizeof(dp));
        for(int i=0;i<=nums[0];i++)
        {
            int prev=i;
            ans+=solve(nums,1,prev);
            ans%=mod;
        }
        return ans;
    }
};
