class Solution:
    MOD = 1_000_000_007

    def reset_dp(self, n):
        self.dp = [[[-1] * 51 for _ in range(51)] for _ in range(n)]

    def get_ans(self, nums, idx, p1, p2):
        if idx >= len(nums):
            return 1
        if self.dp[idx][p1][p2] != -1:
            return self.dp[idx][p1][p2]

        total = 0
        for i in range(nums[idx] + 1):
            v1 = i
            v2 = nums[idx] - i
            if v1 >= p1 and v2 <= p2:
                total = (total + self.get_ans(nums, idx + 1, v1, v2)) % Solution.MOD

        self.dp[idx][p1][p2] = total
        return total

    def countOfPairs(self, nums):
        n = len(nums)
        self.reset_dp(n)
        return self.get_ans(nums, 0, 0, 50)
