class Solution {
public:
     int mod = 1e9 + 7;
    vector<vector<vector<int>>> dp;
    void filldp(vector<int> & arr){
            int n = arr.size();
            for (int i = 1; i < n; ++i) {            
            for (int fir = 0; fir <= arr[i]; ++fir) {

                 int sec = arr[i] - fir;
                 if(sec>=0 && sec <= 50) {

                    for(int firpre = 0; firpre <= arr[i - 1]; firpre++) {

                        int secpre = arr[i - 1] - firpre;
                        if(secpre >= 0 && secpre <= 50) {

                            if(fir >=firpre && sec<= secpre){
                                dp[i][fir][sec] = (dp[i][fir][sec] +dp[i-1][firpre][secpre]) % mod;
								dp[i][fir][sec] %= mod;
                            }

                        }

                    }
                }
            }
        }
    }   
    int countOfPairs(vector<int> &arr){

        int n = arr.size();
        dp.resize(n, vector<vector<int>>(51, vector<int>(51, 0)));

        for (int fir = 0; fir<=arr[0]; fir++) {
            int sec = arr[0]-fir;
            if (sec >= 0 && sec <= 50) {
                dp[0][fir][sec] = 1;
            }
        }

       filldp(arr);

        int ans = 0;
        for(int fir = 0; fir <=arr[n-1]; fir++) {
            int sec = arr[n-1] - fir;
            if(sec>= 0 && sec<=50) {
                ans = (ans + dp[n-1][fir][sec]) %mod;
				ans %= mod;
            }
        }

        return ans%mod;        
    }
};
