MOD = (10**9 + 7)

class Solution:
    def countOfPairs(self, nums: List[int]) -> int:

        dp = defaultdict(lambda: defaultdict(lambda: defaultdict(int)))
        for i in range(0, nums[0]+1):
            dp[0][i][nums[0] - i] = 1
        for i in range(1, len(nums)):
            for arr1_last, xx in dp[i-1].items():
                assert len(xx) == 1
                arr2_last, count = next(iter(xx.items()))
                for arr1_next in range(arr1_last, nums[i]+1):
                    arr2_next = nums[i] - arr1_next
                    if arr2_next <= arr2_last:
                        dp[i][arr1_next][arr2_next] = (dp[i][arr1_next][arr2_next] + count) % MOD


        total = 0
        for arr1_last, xx in dp[len(nums)-1].items():
            arr2_last, count = next(iter(xx.items()))
            total = (total + count) % MOD

        return total
