class Solution {
public:


const int mod = 1e9 + 7;
int n;


const int max_val = 51; 
vector<vector<vector<int>>> memo;

int rec(int idx, int prev1, int prev2, vector<int>& nums) {
    if (idx == n) return 1;

    if (memo[idx][prev1][prev2] != -1) 
        return memo[idx][prev1][prev2];

    int ans = 0;

    for (int i = 0; i <= nums[idx]; i++) {
        if (i >= prev1 && (nums[idx] - i) <= prev2) {
            ans = (ans + rec(idx + 1, i, nums[idx] - i, nums)) % mod;
        }
    }

    memo[idx][prev1][prev2] = ans;
    return ans;
}

int countOfPairs(vector<int>& nums) {
    n = nums.size();
    if (n == 0) return 0;


    memo = vector<vector<vector<int>>>(n, vector<vector<int>>(max_val, vector<int>(max_val, -1)));

    return rec(0, 0, 50, nums);
}


};
