class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int M = 1e9 + 7;
        int n = nums.size();
        vector<vector<int>> d(n, vector<int>(101, 0));
        for (int a = 0; a <= nums[0]; ++a) {
            d[0][a] = 1;
        }
        for (int i = 1; i < n; ++i) {
            for (int a = 0; a <= nums[i]; ++a) {
                for (int p = 0; p <= a; ++p) {
                    if (nums[i-1] - p >= nums[i] - a) {
                        d[i][a] = (d[i][a] + d[i-1][p]) % M;
                    }
                }
            }
        }
        int r = 0;
        for (int a = 0; a <= nums[n-1]; ++a) {
            r = (r + d[n-1][a]) % M;
        }
        return r;
    }
};
