class Solution {
public:
    int mod=1e9+7;

    long long int solve(long long arr[][51][51],vector<int>&nums,int i,int fs1,int fs2)
    {
           if(i>=nums.size())
               return 1;

          if(arr[i][fs1][fs2]!=-1)
              return arr[i][fs1][fs2];

         int pos=fs1;
        long long int cnt=0;
        while(pos<=nums[i])
        {
              int pos2=nums[i]-pos;
            if(pos2<=fs2)
            {
                auto x= solve(arr,nums,i+1,pos,pos2);
                cnt=(cnt+x)%mod;

            }
            pos++;
        }

        arr[i][fs1][fs2]=(cnt)%mod;

        return arr[i][fs1][fs2];


    }

    int countOfPairs(vector<int>& nums) {

        int n=nums.size();

        long long arr[n+1][51][51];

        memset(arr,-1,sizeof(arr));

        long long int ans=0;

        int pos=0;

        while(pos<=nums[0])
        {    
              long long int x= solve(arr,nums,1,pos,nums[0]-pos);

               ans=(ans+x)%mod;
            pos++;
        }

        return ans;



    }
};
