#define great long long int
class Solution {
public:
    int countOfPairs(vector<int>& v) {
        int size = v.size();
        vector<vector<vector<great>>> vec(size, vector<vector<great>>(51, vector<great>(51, -1)));

        function<great(int, int, int)> sol = [&](int ind, int val1, int val2) -> great {
            if (ind >= v.size()) return 1;
            great res = 0;
            great mval = 1e9+7; 
            if (vec[ind][val1][val2] != -1) return vec[ind][val1][val2];
            for (int i = val1; i <= v[ind]; i++) {
                for (int j = val2; j >= 0; j--) {
                    if (i + j == v[ind]) {
                        res += sol(ind + 1, i, j);
                        res = res % mval;
                    }
                }
            }
            return vec[ind][val1][val2] = res;
        };

        return sol(0, 0, v[0]);
    }
};
