class Solution {
public:
    int n;
    vector<int>nums;
    int mod = 1e9 + 7;

    int dp[2020][51];

    int rec(int level,int x){
        // base condition
        if(level == n){
            return 1;
        }

        // prunning

        // cache check
        if(dp[level][x] != -1){
            return dp[level][x];
        }

        // transition
        int ans = 0;
        int s = (level > 0) ? nums[level - 1] : 1e9;
        // if(level == 0) 
        int st = max(nums[level] - s + x , x);
        for(int i = st;i<=nums[level] ; i++){
            ans = (ans%mod +  rec(level + 1 , i)%mod )%mod;
        }

        // save and return
        return dp[level][x] = ans%mod;

    }

    int countOfPairs(vector<int>& num) {
        nums = num;
        n = nums.size();
        memset(dp,-1,sizeof(dp));
        return rec(0,0)%mod;
    }
};
