class Solution {
public:
    int dp[2001][52][52];
    const int mod = 1e9+7;
    int rec(vector<int> &v,int i,int prev,int vminus1){
        if(i==v.size()) return 1;
        if(dp[i][prev][vminus1]!=-1) return dp[i][prev][vminus1];
        int ans=0;
        for(int idx=0;idx<=v[i];idx++){
            if(prev<=idx && vminus1-prev>=v[i]-idx)ans= (ans+rec(v,i+1,idx,v[i]))%mod;
        }
        return dp[i][prev][vminus1]=ans;
    }
    int countOfPairs(vector<int>& v) {
        memset(dp,-1,sizeof(dp));
        int ans = rec(v,0,0,51);
        return ans;
    }
};
