class Solution {
    int MOD=1e9+7;
    int rec(int i,int prev,vector<int>&nums,vector<vector<int>>&dp)
    {
        // cout<<i<<prev<<endl;
        if(i==nums.size())return 1;
        if(dp[i][prev]!=-1)return dp[i][prev];
        int count=0;
        for(int j=prev;j<=nums[i];j++)
        {
            if(i==0 || (nums[i-1]-prev)>=(nums[i]-j))
            count=(count+rec(i+1,j,nums,dp))%MOD;
        }
        return dp[i][prev]=count%MOD;
    }
public:
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<int>>dp(n,vector<int>(51,-1));
        return rec(0,0,nums,dp);
    }
};
