class Solution {
public:
    vector<vector<int>>dp;
    long long M=1e9+7;
    int countOfPairs(vector<int>& nums) {

        vector<int>v1;
        dp.resize(2001,vector<int>(55,-1));
        // return f(0,0,nums,v1);
        return f(0,0,nums);
    }
    // int f(int prev1,int ptr,vector<int>&v,vector<int>&v1)
    int f(int prev1,int ptr,vector<int>&v)
    {
        if (ptr==v.size())
        {
            // for(auto &it:v1)
            // {
            //     cout<<it<<" ";
            // }
            // cout<<endl;
            return 1;
        }
        if (dp[ptr][prev1]!=-1)
        {
            return dp[ptr][prev1];
        }
        int prev2=INT_MAX;
        if (ptr!=0)
        {
            prev2=v[ptr-1]-prev1;
        }
        int ans=0;
        for(int i=prev1;i<=v[ptr];i++)
        {
            int j=v[ptr]-i;
            if (j<=prev2 && j>=0)
            {
                // v1.push_back(i);
                // ans+=f(i,ptr+1,v,v1);
                // v1.pop_back();
                ans=(ans+f(i,ptr+1,v))%(M);

            }  
        }
        return dp[ptr][prev1]=ans;
    }
};
