class Solution {
    Integer[][] dp;
    int MOD = 1_000_000_007;
    public int countOfPairs(int[] nums) {
        int n = nums.length;
        dp = new Integer[n][51];
        return solve(nums, 0, 0) % MOD;
    }
    private int solve(int[] nums, int idx, int prev){
        if(idx == nums.length) return 1;
        if(dp[idx][prev] != null) return dp[idx][prev];
        int count = 0;
        for(int i = prev; i <= nums[idx]; i++){
            if(idx == 0) count = (count + solve(nums, idx + 1, i)) % MOD;
            else if(nums[idx] - i <= nums[idx - 1] - prev) count = (count + solve(nums, idx + 1, i)) % MOD;
        }
        return dp[idx][prev] = count;
    }
}
