class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        mod = 10**9+7
        dp = [[0]*1001 for _ in range(n)]
        for j in range(nums[0]+1):
            dp[0][j] = j+1
        for j in range(nums[0]+1, 1001):
            dp[0][j] = dp[0][j-1]
        for i in range(1, n):
            for j in range(nums[i]+1):
                d = 0
                k = min(j, nums[i-1]-nums[i]+j)
                if k >= 0:
                    d = dp[i-1][k]
                dp[i][j] = dp[i][j-1]+d
                dp[i][j] %= mod
            for j in range(nums[i]+1, 1001):
                dp[i][j] = dp[i][j-1]

        return dp[-1][-1]%mod
