const int MOD = 1e9 + 7;
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int f[n][51];
        memset(f, 0, sizeof(f));
        for (int i = 0; i <= nums[0]; i++) {
            f[0][i] = 1;
        }
        for (int i = 1; i < n; i++) {
            for (int j = 0; j <= nums[i]; j++) {
                int t = nums[i] - j;
                for (int k = 0; k <= j; k++) {
                    if (nums[i - 1] - k >= t) {
                        f[i][j] = (f[i][j] + f[i - 1][k]) % MOD;
                    }
                }
            }
        }
        int res = 0;
        for (int i = 0; i <= nums[n - 1]; i++) {
            res = (res + f[n - 1][i]) % MOD;
        }
        return res;
    }
};
