class Solution {
public:
    int help(int a,vector<int>& nums,int indx,vector<vector<int>>& dp)
    {
        int const mod=1e9+7;
        int ans=0;
        int n=nums.size();
        if(n==indx)
            return 1;
        if(dp[a][indx]!=-1)
            return dp[a][indx];
        for(int i=0;i<=50;i++)
        {
            if(indx==0)
            {
                if((nums[indx]-i)>=0)
                    ans=(ans%mod+help(i,nums,indx+1,dp)%mod)%mod;
            }
            else if((nums[indx]-i)>=0&&i>=a&&(nums[indx]-i)<=(nums[indx-1]-a))
            {
                ans=(ans%mod+help(i,nums,indx+1,dp)%mod)%mod;
            }
        }
        return dp[a][indx]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<int>> dp(51,vector<int>(n,-1));
        return help(0,nums,0,dp);
    }
};
