class Solution {
    private static final int MOD = 1_000_000_007;

    public int countOfPairs(int[] nums) {
        int n = nums.length;
        int[][][] dp = new int[n + 1][51][51];
        for (int[][] row : dp) {
            for (int[] col : row) {
                Arrays.fill(col, -1);
            }
        }
        return solve(nums, 0, 0, 50, dp);
    }

    private int solve(int[] nums, int ind, int pr1, int pr2, int[][][] dp) {
        if (ind >= nums.length) {
            return 1;
        }

        if (dp[ind][pr1][pr2] != -1) {
            return dp[ind][pr1][pr2];
        }

        int ans = 0;
        for (int i = 0; i <= nums[ind]; i++) {
            int incVal = i;
            int devVal = nums[ind] - i;
            if (incVal >= pr1 && devVal <= pr2) {
                ans = (ans + solve(nums, ind + 1, incVal, devVal, dp)) % MOD;
            }
        }

        dp[ind][pr1][pr2] = ans;
        return ans;
    }
}
