class Solution:
    def countOfPairs(self, nums: List[int]) -> int:

        n = len(nums)

        mod = 10**9 + 7

        nums.append(nums[0])

        dp = {}

        def state(i, a):
            if i == n:
                return 1

            if (i, a) in dp:
                return dp[(i, a)]

            count = 0
            for choice in range(max(a, nums[i] - nums[i-1] + a), nums[i]+1):
                count += state(i+1, choice)

            dp[(i, a)] = count % mod
            return dp[(i, a)]

        return state(0, 0)
