class Solution {
public:
    int dp[2001][51][51],n,M=1e9+7;
    int f(vector<int>&a,int i,int j,int k){
        if(i==n) return 1;
        if(dp[i][j][k]!=-1) return dp[i][j][k];

        int ans=0,mx=max(j,a[i]-k);
        for(int x=mx;x<=a[i];x++){
             ans=(ans%M+f(a,i+1,x,a[i]-x)%M)%M;
        }
        return dp[i][j][k]=ans%M;
    }
    int countOfPairs(vector<int>& a) {
        n=a.size();
        memset(dp,-1,sizeof(dp));
        return f(a,0,0,50);
    }
};
