class Solution:
    def countOfPairs(self, nums: List[int]) -> int:

        n = len(nums)
        mod = (10**9) + 7
        @lru_cache(None)
        def backtrack(i, prev1, prev2):
            if(i==n):
                return 1
            ans = 0
            for j in range(nums[i]+1):
                if(prev1==-1 or prev2==-1 or (j>=prev1 and (nums[i]-j)<=prev2)):
                    ans += backtrack(i+1, j, (nums[i]-j)) % mod
            return ans% mod
        return backtrack(0,-1,-1)
