const int MOD = 1000000007;
class Solution {
public:
    vector<vector<vector<int>>> dp;

    void init() {
        dp.clear();
    }

    int find_pairs(vector<int>& nums, int idx, int prev1, int prev2) {

        int ans = 0;
        if (idx >= nums.size()) {
            return 1;
        }

        if (dp[idx][prev1][prev2] != -1) {
            return dp[idx][prev1][prev2];
        }

        for (int i = 0; i <= nums[idx]; ++i) {

            int arr2_val = nums[idx] - i;
            int arr1_val = i;
            if (arr1_val >= prev1 && arr2_val <= prev2) {
                ans = (ans + find_pairs(nums, idx + 1, arr1_val, arr2_val)) % MOD;
            }
        }

        dp[idx][prev1][prev2] = ans;
        return ans;
    }

    int countOfPairs(vector<int>& nums) {
        dp.resize(nums.size(), vector<vector<int>>(51, vector<int>(51, -1))); 
        return find_pairs(nums, 0, 0, 50);
    }
};
