class Solution {
public:
    int mod = 1000000007;
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();

        vector<vector<int>> dp(n,vector<int> (51,0));

        for(int i=0;i<=nums[0];++i){
            dp[0][i] = 1;
        }

        for(int i=1;i<n;++i){
            for(int j=0;j<=nums[i];++j){
                int mn = min(j,nums[i-1]-nums[i]+j);
                if(mn<0) continue;
                // cout<<i<<" "<<j<<" "<<mn<<endl;
                for(int k=0;k<=mn;++k){
                    dp[i][j] = (dp[i][j]%mod + dp[i-1][k]%mod)%mod;
                }
            }
        }

        int ans = 0;
        for(int i=0;i<=50;++i){
            ans = (ans + dp[n-1][i]%mod)%mod;
        }

        return ans;
    }
};
