int mod = 1e9 + 7;
int dp[2000][51][51];
class Solution {
    int solve(vector<int> &nums, int i, int j, int k) {
        if(i == nums.size())
            return 1;

        if(dp[i][j][k] != -1)
            return dp[i][j][k];

        long long ans = 0;
        for(int t = 0; t <= nums[i]; t++) {
            if(t >= j && nums[i] - t <= k)
                ans = (ans + solve(nums, i + 1, t, nums[i] - t)) % mod;
        }

        return dp[i][j][k] = ans;
    } 
public:
    int countOfPairs(vector<int>& nums) {
        for(int i = 0; i < nums.size(); i++)
            for(int j = 0; j < 51; j++)
                for(int k = 0; k < 51; k++)
                    dp[i][j][k] = -1;
        return solve(nums, 0, 0, 50);
    }
};
