#define MOD 1000000007
class Solution {
public:
    int dp[2001][51][51];
    int f(int i, int last1, int last2, vector<int>& nums) {
        if (i == nums.size()) {
            return 1;
        }
        if (dp[i][last1][last2] != -1) {
            return dp[i][last1][last2];
        }
        int cnt = 0;
        for (int a = last1; a <= nums[i]; ++a) {
            int b = nums[i] - a;
            if (b <= last2) {
                cnt = (cnt + f(i + 1, a, b, nums)) % MOD;
            }
        }
        return dp[i][last1][last2] = cnt;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof(dp));
        return f(0, 0, 50, nums);
    }
};
