class Solution {
public:
    vector<vector<vector<int>>> dp;
    const int mod = 1e9 + 7;

    int f(vector<int>& nums, int ind, int prev1, int prev2) {
        int n = nums.size();
        if (ind == n) {
            return 1; 
        }

        if (dp[ind][prev1][prev2] != -1) {
            return dp[ind][prev1][prev2];
        }

        int res = 0;
        for (int i = prev1; i <= nums[ind]; ++i) {
            int j = nums[ind] - i;
            if (j <= prev2) {
                res = (res + (f(nums, ind + 1, i, j) % mod)) % mod;
            }
        }

        return dp[ind][prev1][prev2] = res % mod;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        dp.resize(n, vector<vector<int>>(51, vector<int>(51, -1)));

        return f(nums, 0, 0, 50);
    }
};
