class Solution {
    public int countOfPairs(int[] nums) {
        int n = nums.length;
        int dp[][][] =  new int[n][51][51];
        for(int i=0;i<n;i++)
            for(int j=0;j<51;j++)
                Arrays.fill(dp[i][j],-1);
        return helper(0,nums,0,50,dp);
    }
    int helper(int i,int nums[] , int last1, int last2,int dp[][][]){
        if(i==nums.length)
            return 1;
        if(dp[i][last1][last2]!=-1) return dp[i][last1][last2]; 
        long ans = 0;    
        for(int j=last1;j<=nums[i];j++){
            if(nums[i]-j >=0 && nums[i]-j <= last2)
                ans = (ans+helper(i+1,nums,j,nums[i]-j,dp)) % 1000000007;
        }
        return dp[i][last1][last2] = (int)ans;    
    }
}
