class Solution {
    static const int MOD = 1000000007;
    using ll = long long;
private:
   ll pairs(vector<int>& nums, int ind, int m, int k, vector<vector<vector<ll>>>& dp) {
        if (ind >= nums.size()) return 1;  
        if (dp[ind][m][k] != -1) return dp[ind][m][k];  

        ll Cnt_pairs = 0;
        for (int i = m; i <= nums[ind]; i++) {
            for (int j = k; j >= 0; j--) {
                if (i + j == nums[ind]) {
                    Cnt_pairs = (Cnt_pairs + pairs(nums, ind + 1, i, j, dp)) % MOD;
                }
            }
        }
        dp[ind][m][k] = Cnt_pairs;
        return Cnt_pairs;
    }

public:
    int countOfPairs(vector<int>& nums) {
        int size = nums.size();
        vector<vector<vector<ll>>> dp(size, vector<vector<ll>>(51, vector<ll>(51, -1)));
        return static_cast<int>(pairs(nums, 0, 0, nums[0], dp));
    }
};
