class Solution {
public:
int mod = 1e9+7;
    int dp[2005][55][55];
    int f(vector<int> &nums,int i, int prev1,int prev2){
        if(i==nums.size()) return 1;
        if(dp[i][prev1][prev2]!=-1) return dp[i][prev1][prev2];
        int ans = 0;
        for(int j=0;j<=50;j++){
            if(j<prev1 or nums[i]-j>prev2) continue;
            if(j>nums[i] or j<0 or nums[i]-j>nums[i] or nums[i]-j<0) continue;
            ans+=f(nums,i+1,j,nums[i]-j);
            ans%=mod;
        }
        return dp[i][prev1][prev2]= ans%mod;
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        memset(dp,-1,sizeof dp);
        return f(nums,0,0,52);
    }
};
