class Solution {
public:
    int dp[52][2001];
    int mod = 1e9 + 7;
    int rec(int idx,int last_arr1,vector<int> &nums)
    {
        if(idx >= nums.size())
            return 1;
        else if(dp[last_arr1+1][idx] != -1)
            return dp[last_arr1+1][idx];
        else 
        {
            int last_arr2 = idx == 0 ? 1e9 : nums[idx - 1] - last_arr1;
            int cur_arr2;
            int ans = 0;
            //cout<<idx<<" "<<last_arr1<<" "<<last_arr2<<endl;
            int lim = last_arr1 == -1 ? 0 : last_arr1;
            for(int i = lim;i<=nums[idx];i++)
            {
                cur_arr2 = nums[idx] - i;
                if(cur_arr2 > last_arr2)
                    continue;
                else if(cur_arr2 < 0)
                    break;
              //  cout<<"idx = "<<idx<<" "<<i<<" "<<cur_arr2<<endl;
                ans = (ans + rec(idx+1,i,nums)%mod)%mod;
            }
            return dp[last_arr1+1][idx] = ans;
        }
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof(dp));
        return rec(0,-1,nums);
    }
};
