class Solution {
public:
    int findAns(int a1, int a2, int ind, int n,vector<int> &nums,vector<vector<vector<int> > > &dp) {
        if(ind==n) {
            dp[ind][a1][a2] = 1;
            return 1;
        }
        if(dp[ind][a1][a2]!=-1) {
            return dp[ind][a1][a2];
        }
        int ans = 0;
        for(int i=a1;i<=nums[ind];i++) {
            if(nums[ind]-i>a2) {
                continue;
            }
            int x = findAns(i,nums[ind]-i,ind+1,n,nums,dp);
            ans = (ans%1000000007 + x%1000000007)%1000000007;
        }
        return dp[ind][a1][a2] = ans%1000000007;

    }
    int countOfPairs(vector<int>& nums) {
        int ans = 0;
        vector<vector<vector<int> > > dp(nums.size()+1,vector<vector<int> > (51,vector<int> (51,-1)));
        for(int i=0;i<=nums[0];i++) {
            int x = findAns(i,nums[0]-i,1,nums.size(),nums,dp);
            ans = (ans%1000000007 + x%1000000007)%1000000007;
        }
        return ans%1000000007;
    }
};
