const int MOD = 1e9 + 7;

class Solution {
    int dp[2001][51][51];  

    int helper(int ind, int parent1, int parent2, vector<int>& arr) {
        int n = arr.size();
        if (ind == n) return 1;

        if (dp[ind][parent1][parent2] != -1) return dp[ind][parent1][parent2];

        int cnt = 0;
        for (int i = 0; i <= arr[ind]; i++) {
            int a = arr[ind] - i;
            if (i >= parent1 && a <= parent2) {
                cnt = (cnt + helper(ind + 1, i, a, arr)) % MOD;
            }
        }

        return dp[ind][parent1][parent2] = cnt;
    }

public:
    int countOfPairs(vector<int>& arr) {
        memset(dp, -1, sizeof(dp));  

        return helper(0, 0, 50, arr);
    }
};
