#define ll long long int

class Solution {
public:
    int countOfPairs(vector<int>& values) {
        int n = values.size();
        vector<vector<vector<ll>>> dp(n, vector<vector<ll>>(51, vector<ll>(51, -1)));

        function<ll(int, int, int)> countPairs = [&](int index, int firstVal, int secondVal) -> ll {
            if (index >= n) return 1;
            ll totalWays = 0;
            ll mod = 1e9 + 7;
            if (dp[index][firstVal][secondVal] != -1) return dp[index][firstVal][secondVal];
            for (int i = firstVal; i <= values[index]; i++) {
                for (int j = secondVal; j >= 0; j--) {
                    if (i + j == values[index]) {
                        totalWays += countPairs(index + 1, i, j);
                        totalWays %= mod;
                    }
                }
            }
            return dp[index][firstVal][secondVal] = totalWays;
        };

        return countPairs(0, 0, values[0]);
    }
};
