class Solution {
    int MOD = 1_000_000_007;
    int[][][] dp;
    private int solve(int[] nums,int idx,int p1,int p2){
        if(idx>=nums.length){
            return 1;
        }

        if(dp[idx][p1][p2]!=-1){
            return dp[idx][p1][p2];
        }

        int ans = 0;
        for(int i=0;i<=nums[idx];i++){
            int a1=i;
            int a2=nums[idx]-i;
            if(a1>=p1&&a2<=p2){
                ans=(ans+solve(nums,idx+1,a1,a2))%MOD;
            }
        }
        dp[idx][p1][p2]=ans;
        return ans;
    }

    public int countOfPairs(int[] nums){
        int n = nums.length;
        dp = new int[n][51][51];
        for(int i=0;i<n;i++){
            for(int j=0;j<51;j++){
                for(int k=0;k<51;k++){
                    dp[i][j][k]=-1;
                }
            }
        }
        return solve(nums,0,0,50);
    }
}
