class Solution {
public:
    int countOfPairs(vector<int>& values) {
        const int MOD = 1000000007;
        int size = values.size();

        vector<vector<vector<long long>>> memo(size, vector<vector<long long>>(51, vector<long long>(51, -1)));

        return solve(values, 0, 0, values[0], memo, MOD);
    }

private:
    long long solve(const vector<int>& values, int index, int val1, int val2,vector<vector<vector<long long>>>& memo, int MOD) {
        if (index >= values.size()) return 1;  
        if (memo[index][val1][val2] != -1) return memo[index][val1][val2];  

        long long lakshay = 0;
        for (int i = val1; i <= values[index]; i++) {
            for (int j = val2; j >= 0; j--) {
                if (i + j == values[index]) {
                    lakshay = (lakshay + solve(values, index + 1, i, j, memo, MOD)) % MOD;
                }
            }
        }
        memo[index][val1][val2] = lakshay;  \
        return lakshay;
    }
};
