class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int memo[2005][52][52];
        memset(memo, -1, sizeof(memo));
        function<int(int,int,int)>dfs = [&](int idx, int a, int b) {
            if (idx >=n) {
                return 1;
            }
            if (memo[idx][a][b]!=-1) {
                return memo[idx][a][b];
            } 
            int tot = 0 ;
            for(int i = a; i <= nums[idx]; i++) {
                for(int j=b; j>=0;j--) {
                    if (i+j == nums[idx]) {
                        tot += dfs(idx+1,i,j)%1000000007;
                        tot%=1000000007;
                    }
                }
            }
            return memo[idx][a][b] = tot;
        };
        return dfs(0, 0, nums[0]);


    }
};
