class Solution {
public:
    int dp[2003][53][53];
    int mod=1e9+7;
    int f(int i,int preL,int preR,vector<int>&nums){
        if(i==nums.size())return 1;
        if(dp[i][preL][preR]!=-1)return dp[i][preL][preR];
        long long ct=0;
        for(int j=0;j<=nums[i];j++){
            if(j>=preL && nums[i]-j<=preR)ct+=f(i+1,j,nums[i]-j,nums);
            ct%=mod;
        }
        return dp[i][preL][preR]= ct;
    }

    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof(dp));
        return f(0,0,50,nums);
    }
};
