class Solution {
public:

    using ll = long long int;
    const ll mod = 1e9+7;
    ll dp[2010][55];
    ll rec(ll lvl,ll prev,vector <int>&nums){
        if(lvl==nums.size()) return 1;
        if(dp[lvl][prev]!=-1) return dp[lvl][prev];
        ll prev2 = nums[lvl-1]-prev;
        ll ans = 0;
        for(ll i = prev;i<=nums[lvl];i++){
            ll j = nums[lvl]-i;
            if(j>=0 and j<=prev2) (ans+= rec(lvl+1,i,nums))%=mod;
        } 
        return dp[lvl][prev] = ans;
    }
    int countOfPairs(vector<int>& nums) {
        ll n= nums.size();
        for(int i = 0;i<n+1;i++){
            for(int j = 0;j<52;j++) dp[i][j] = -1;
        }
        ll ans = 0;
        for(int i = 0;i<=nums[0];i++){
            (ans+= rec(1,i,nums))%=mod;
        }
        return ans;
    }
};
