class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int MOD = 1'000'000'007;
        int size = nums.size();

        vector<vector<vector<long>>> memo(
            size, vector<vector<long>>(51, vector<long>(51, -1)));

        return (int)solve(nums, 0, 0, nums[0], memo, MOD);
    }

    long solve(vector<int>& nums, int index, int val1, int val2,
               vector<vector<vector<long>>>& memo, const int MOD) {
        if (index >= nums.size())
            return 1;
        if (memo[index][val1][val2] != -1)
            return memo[index][val1][val2];

        long result = 0;
        for (int i = val1; i <= nums[index]; i++) {
            for (int j = val2; j >= 0; j--) {
                if (i + j == nums[index]) {
                    result =
                        (result + solve(nums, index + 1, i, j, memo, MOD)) %
                        MOD;
                }
            }
        }
        memo[index][val1][val2] = result;
        return result;
    }
};
