class Solution {
public:
int ans=0;
int mod=1e9+7;
   int f(int ind, vector<int>& nums, int n, int prev, vector<vector<int>>& dp) {
    if (ind == n) {
        return 1; 
    }

    if (dp[ind][prev+1] != -1) {
        return dp[ind][prev+1]; 
    }

    int result = 0;
    int st = max(0, prev);
    for (int st = max(0, prev); st <= nums[ind]; st++) {
        if (prev == -1 || (nums[ind] - st <= nums[ind - 1] - prev)) {
            result = (result + f(ind + 1, nums, n, st, dp)) % mod;
        }
    }
        dp[ind][prev+1] = result; 

    return result;
}

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int maxi=0;
        for(auto e:nums)
        {
            maxi=max(maxi,e);
        }
    vector<vector<int>> dp(n, vector<int>(maxi + 2, -1));
    return f(0, nums, n, -1, dp);
    }
};
