class Solution {
public:
    int solve(int i, int v1, int v2, vector<int>&nums, vector<vector<vector<int>>>&dp){
        if(nums.size()<=i) return 1;
        //ans ko zero
        int ans=0;
        int mod=1e9+7;
        if(dp[i][v1][v2]!=-1) return dp[i][v1][v2];
        int a=v1;
        //dono values ke bich me sab check karna
        while(nums[i]>=a){
            int b=v2;
            while(b>=0){
                //nums of index check karna hai
                if(nums[i]==a+b){
                    //ans me add karna hai 
                    ans+=solve(i+1,a,b,nums,dp);
                    //ans ko mod se divide
                    ans=ans%mod;
                }
                b--;
            }
            a++;

        }
        return dp[i][v1][v2]=ans;


    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        int p=50;
        //dp vector create kara hai
        vector<vector<vector<int>>>dp(n,vector<vector<int>>(p+1,vector<int>(p+1,-1)));
        return solve(0,0,nums[0],nums,dp);
    }
};
