#include <vector>
#include <functional>
using namespace std;

#define MOD 1000000007

class Solution {
public:
    int countOfPairs(vector<int>& values) {
        int size = values.size();
        vector<vector<vector<long long>>> memo(size, vector<vector<long long>>(51, vector<long long>(51, -1)));

        function<long long(int, int, int)> solve = [&](int index, int val1, int val2) -> long long {
            if (index >= size) return 1;

            if (memo[index][val1][val2] != -1) return memo[index][val1][val2];

            long long result = 0;
            for (int i = val1; i <= values[index]; i++) {
                for (int j = val2; j >= 0; j--) {
                    if (i + j == values[index]) {
                        result = (result + solve(index + 1, i, j)) % MOD;
                    }
                }
            }

            return memo[index][val1][val2] = result;
        };

        return solve(0, 0, values[0]);
    }
};
