class Solution {
    private:
 const int MOD = 1e9+7;

    long long solve( vector<int>& nums, int ind, int v1, int v2, vector<vector<vector<long long>>> &dp) {
        if (ind >= nums.size()) {
            return 1;
        }
        if (dp[ind][v1][v2] != -1) 
        {return dp[ind][v1][v2] ; }

        long long ans = 0;
        for (int i = v1; i <= nums[ind]; i++) {
            for (int j = v2; j >= 0; j--) {
                if (i + j == nums[ind]) {
                    ans = (ans + solve(nums, ind + 1, i, j, dp)) % MOD;
                }
            }
        }
       dp[ind][v1][v2]  = ans;
        return ans;
    }
public:
    int countOfPairs(vector<int>& nums) {
         int size = nums.size();
        vector<vector<vector<long long>>> dp(size, vector<vector<long long>>(51, vector<long long>(51, -1)));

        return (int) solve(nums, 0, 0, nums[0], dp);




    }
};
