class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int size = nums.size();
        vector<vector<vector<long long>>> memo(size, vector<vector<long long>>(51, vector<long long>(51, -1)));

        function<long long(int, int, int)> solve = [&](int index, int val1, int val2) -> long long {
            if (index >= nums.size()) return 1;
           long long result = 0;
           long long mod_val = 1e9+7; 
            if (memo[index][val1][val2] != -1) return memo[index][val1][val2];
            for (int i = val1; i <= nums[index]; i++) {
                for (int j = val2; j >= 0; j--) {
                    if (i + j == nums[index]) {
                        result += solve(index + 1, i, j);
                        result = result % mod_val;
                    }
                }
            }
            return memo[index][val1][val2] = result;
        };

        return solve(0, 0, nums[0]);
    }
};
