#include <cstring> // for memset
#include <vector>
using namespace std;

class Solution {
public:
    const int MOD = 1e9 + 7;
    long solve(int index, int val1, int val2, vector<int>& values,
               vector<vector<vector<long>>>& dp) {
        if (index >= values.size())
            return 1;
        if (dp[index][val1][val2] != -1)
            return dp[index][val1][val2];
        long res = 0;
        for (int i = val1; i <= values[index]; i++) {
            for (int j = val2; j >= 0; j--) {
                if (i + j == values[index]) {
                    res = (res + solve( index + 1, i, j,values, dp)) % MOD;
                }
            }
        }
        dp[index][val1][val2] = res;
        return res;
    }
    int countOfPairs(vector<int>& values) {
        int size = values.size();
        vector<vector<vector<long>>> dp(
            size, vector<vector<long>>(51, vector<long>(51, -1)));
        return (int)solve(0, 0, values[0], values, dp);
    }
};
